from T_RHIP_HOSPITAL_EMP he
where 1=1
and (he.DISABLED is null or he.DISABLED=0)

    and he.HOSPITAL_ID=#{hospitalId,jdbcType=DECIMAL}


    and he.DEPT_NO=#{deptId,jdbcType=DECIMAL}

